import React, { useState } from 'react';

const Calculator = () => {
  const [result, setResult] = useState('');

  const handleClick = (e) => {
    setResult(result.concat(e.target.name));
  };

  const clear = () => {
    setResult('');
  };

  const calculate = () => {
    try {
      let expression = result.replace(/%/g, '/100').replace(/[^-()\d/*+.]/g, '');
      setResult(eval(expression).toString());
    } catch (error) {
      setResult('Error');
    }
  };

  const deleteLastChar = () => {
    setResult(result.slice(0, -1));
  };


  return (
    <div className="calculator">
      <h1 className='my-calculator'>My Calculator</h1>
      <input type="text" value={result} />
      <div className="keypad">
        <button className="highlight" name="7" onClick={handleClick}>7</button>
        <button className="highlight" name="8" onClick={handleClick}>8</button>
        <button className="highlight" name="9" onClick={handleClick}>9</button>
        <button name="+" onClick={handleClick}>+</button>
        <button className="highlight" name="4" onClick={handleClick}>4</button>
        <button className="highlight" name="5" onClick={handleClick}>5</button>
        <button className="highlight" name="6" onClick={handleClick}>6</button>
        <button name="-" onClick={handleClick}>-</button>
        <button className="highlight" name="1" onClick={handleClick}>1</button>
        <button className="highlight" name="2" onClick={handleClick}>2</button>
        <button className="highlight" name="3" onClick={handleClick}>3</button>
        <button name="*" onClick={handleClick}>*</button>
        <button className="highlight" name="0" onClick={handleClick}>0</button>
        <button name="." onClick={handleClick}>.</button>
        <button name="/" onClick={handleClick}>/</button>
        <button name="%" onClick={handleClick}>%</button> {/* Percentage button */}
        <button name="(" onClick={handleClick}>(</button> {/* Opening bracket button */}
        <button name=")" onClick={handleClick}>)</button> {/* Closing bracket button */}
        <button onClick={togglePositiveNegative}>+/-</button> {/* Positive/Negative button */}
        <button onClick={deleteLastChar}>&lt;</button>
        <div>
          <button className="clear-button" onClick={clear}>C</button>
        </div>
        <div>
          <button className="equal-button" onClick={calculate}>=</button>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
